    function clickPad(key) {
        var txtPassword = document.getElementById("miniPassword");
        txtPassword.focus();
        if (key == 'Clear') {
            txtPassword.value = "";
        }
        else if (key == 'Backspace') {
            if (txtPassword.value.length > 0) {
                txtPassword.value = txtPassword.value.substring(1, txtPassword.value.length);
            }
        }
        else if (key == 'Close') {
            $("#password_key").hide("fast");
            $("#miniCheckCode").focus();
        }
        else {
            if (txtPassword.value.length == txtPassword.maxLength) {
                return;
            }
            txtPassword.value = key + txtPassword.value;
        }
    }
    var keyboard_show = false;
    function showKeyboard() {
        //document.title = "show " + keyboard_show;
        if (!keyboard_show) {
            //document.title += " do show";
            $("#password_key").show("slow");
        }
    }
    function hideKeyboard() {
        //document.title = "hide " + keyboard_show;
        if (!keyboard_show) {
            $("#password_key").hide("fast");
        }
    }

    function keyboard_mouseover() {
        keyboard_show = true;
        //document.title = keyboard_show;
    }

    function keyboard_mouseleave() {
        keyboard_show = false;
        //document.title = keyboard_show;
    }
    function ShowKey() {
        if ($("#password_key").css("display") == "none") {
            $("#password_key").show();
        }
    }
    //绑定登录类型改变事件
    function signTypeChangeEvent() {
        $("#miniSignType").live("change", function () {
            var type = $(this).val();
            SetPwdTitle(type);
        });
        SetPwdTitle($("#miniSignType").val());
    }

    //绑定登录事件
    function btnMiniLoginEvent() {
        $("#btnMiniLogon").die().live("click", function () {
            var signtype = $("#miniSignType").val();
            var username = $("#miniUserAccount").val();
            var password = $("#miniPassword").val();
            var checkcode = $("#miniCheckCode").val();
            var ssoUrl = $("#hidSsoUrl").val();
            var isLoadFirstLoginPrompt = $("#hidIsLoadFirstLoginPrompt").val();
            //异步提交请求登录验证
            $.post("/Account/MiniCheckIn", { "signtype": signtype, "username": username, "password": password, "checkcode": checkcode, "isUsedKeyPad": false }, function (data) {
                if (data.substr(0, 7) == "success") {
                    if (data.substr(8).toLowerCase() == "true" && isLoadFirstLoginPrompt.toLowerCase() == "true") {
                        RedirectTo(ssoUrl + "/Account/FirstLoginPrompt");
                    }
                    else {
                        window.location.href = "/";
                    }
                }
                else {
                    $("#logonerrormsg").show().text(data);
                    changeCheckCode();
                    $("#miniCheckCode").val("");
                }
            });
        });
    }

    //注册事件
    function btnLogOnRegister() {
        $("#btnLogOn").die().live("click", function () {
            $.get("/Account/Register");
        });
    }

    //回车提交表单进行登录
    function keyEnter(e) {
        //e = (e) ? e : ((window.event) ? window.event : ""); //兼容IE和Firefox获得keyBoardEvent对象
        //var key = e.keyCode ? e.keyCode : e.which; //兼容IE和Firefox获得keyBoardEvent对象的键值
        var isie = (document.all) ? true : false; //判断是IE内核还是Mozilla 
        var key;
        if (isie)
            key = window.event.keyCode; //IE使用windows.event事件 
        else {
            key = e.which; //3个按键函数有一个默认的隐藏变量，这里用e来传递。e.which给出一个索引值给Mo内核（注释1） 
        }
        if (key == 13) {
            $("#btnMiniLogon").focus().click();
        }
    }

