        } else if (type == "SynCard") {
            usernameObj.html("校园卡号：");
            passwordObj.html("查询密码：");
            //pForUserAccountObj.text("请输入校园卡号");
            //pForPasswordObj.text("请输入校园卡查询密码");
        } else if (type == "SynSno") {
            usernameObj.html("学&nbsp;工&nbsp;号：");
            passwordObj.html("查询密码：");
